        static readonly string Word = "XMAS";
        static readonly int WordLength = Word.Length;

        static readonly List<(int dy, int dx)> Directions = new List<(int dy, int dx)>()
    {
        (0, 1),   // Right
        (0, -1),  // Left
        (1, 0),   // Down
        (-1, 0),  // Up
        (1, 1),   // Down-Right
        (-1, -1), // Up-Left
        (1, -1),  // Down-Left
        (-1, 1)   // Up-Right
    };

       public static void Main()
        {
            int count = CountWordOccurrences();
            Console.WriteLine($"Total XMAS found: {count}");
        }

        static int CountWordOccurrences()
        {
            int count = 0;

            for (int y = 0; y < Rows; y++)
            {
                for (int x = 0; x < Cols; x++)
                {
                    foreach (var (dy, dx) in Directions)
                    {
                        if (IsWordAt(y, x, dy, dx))
                            count++;
                    }
                }
            }

            return count;
        }

        static bool IsWordAt(int y, int x, int dy, int dx)
        {
            for (int i = 0; i < WordLength; i++)
            {
                int ny = y + i * dy;
                int nx = x + i * dx;

                if (ny < 0 || ny >= Rows || nx < 0 || nx >= Cols)
                    return false;

                if (Grid[ny][nx] != Word[i])
                    return false;
            }
            return true;
        }
    }  
