        static readonly int rows = grid.Length;
        static readonly int cols = grid[0].Length;

      public  static void Main()
        {
            int count = 0;

            for (int i = 1; i < rows - 1; i++)
            {
                for (int j = 1; j < cols - 1; j++)
                {
                    if (grid[i][j] == 'A' && IsXMas(i, j))
                    {
                        count++;
                    }
                }
            }

            Console.WriteLine("X-MAS appears " + count + " times.");
        }

        static bool IsXMas(int i, int j)
        {
            string[] diagonals = new string[2];

            
            diagonals[0] = $"{grid[i - 1][j - 1]}{grid[i][j]}{grid[i + 1][j + 1]}";

           
            diagonals[1] = $"{grid[i - 1][j + 1]}{grid[i][j]}{grid[i + 1][j - 1]}";

            foreach (var diag in diagonals)
            {
                if (diag != "MAS" && diag != "SAM")
                    return false;
            }

            return true;
        }
    }
