        static bool IsSafe(int[] levels)
        {
            int[] diffs = new int[levels.Length - 1];

            for (int i = 0; i < levels.Length - 1; i++)
            {
                diffs[i] = levels[i + 1] - levels[i];
            }

            bool allIncreasing = diffs.All(d => d > 0);
            bool allDecreasing = diffs.All(d => d < 0);
            bool allDiffsValid = diffs.All(d => Math.Abs(d) >= 1 && Math.Abs(d) <= 3);

            return (allIncreasing || allDecreasing) && allDiffsValid;
        }

        static bool IsSafeWithDampener(string line)
        {
            int[] levels = line.Split(' ').Select(int.Parse).ToArray();

            
            if (IsSafe(levels))
                return true;

           
            for (int i = 0; i < levels.Length; i++)
            {
                var reduced = levels.Where((_, idx) => idx != i).ToArray();
                if (reduced.Length >= 2 && IsSafe(reduced))
                    return true;
            }

            return false;
        }
